mapper.select:
select id,domain,url,type,accessState,registerState,otherState,PR,categoryId,remark from t_resource  where 1 = 1

   id = #{id}


    and   domain = #{domain}


    and   url = #{url}


    and   type = #{type}


    and  accessState = #{accessState}


    and   domain = #{domain}


    and   url = #{url}


    and   pr = #{pr}

select id,domain,url,type,accessState,registerState,otherState,PR,categoryId,remark from t_resource  where 1 = 1

    id = #{id}


    and   domain = #{domain}


    and   url = #{url}


    and   type = #{type}


  and  accessState = #{accessState}


    and   domain = #{domain}


    and   url = #{url}


    and   pr = #{pr}

mapper.update:
update t_resource set id = id

    ,   domain = #{domain}


    ,   url = #{url}


    ,   type = #{type}


   , accessState = #{accessState}


    ,   domain = #{domain}


    ,   url = #{url}


    ,   pr = #{pr}

where id = #{id}
